import { useState } from "react";
import { Button, Dialog, DialogContent, DialogTitle, Divider, List, ListItem, Typography } from "@mui/material";
import { useCartContext } from "../Store/CartProvider.js"
import AddIcon from '@mui/icons-material/Add';
import RemoveIcon from '@mui/icons-material/Remove';

const Cart = ({ isValid, onClose }) => {
  const { cartItems, totalAmount, qty, handleAdd } = useCartContext();


  const handleRemove = () => {

  }

  return (
    <Dialog className="cart-dialog" open={isValid} onClose={onClose}>
      <DialogContent>
        <DialogTitle className="dialog-title">
          <Typography variant="h5">Your Cart</Typography>
        </DialogTitle>
        <List className="cart-items">
          {cartItems.map((item) => {
            return (
              <ListItem 
                className="cart-item" 
                key={item.id} 
                sx={{ display: "flex", justifyContent: "space-between" }}>
                <div className="item-details">
                  <div className="item-name">
                    <p>{item.name}</p>
                  </div>
                  <div className="item-price">
                    <p>${item.price}</p>
                  </div>
                </div>
                <div className="item-qty">
                  <p> x {qty[item.name]}</p>
                </div>
                <div className="item-action">
                  <AddIcon onClick={() => handleAdd(item)} />
                  <RemoveIcon onClick={handleRemove}/>
                </div>
                <div className="total-item-amount">
                  <p>$</p>
                  <span>{qty[item.name] * item.price}</span>
                </div>
              </ListItem>
            )
          })}
        </List>
        <Divider />
        <div className="total-amount">
          <p>Total Amount:</p>
          <p>${totalAmount}</p>
        </div>
        <Divider />
        <div className="cart-action-button">
          <Button onClick={onClose}>Close</Button>
          <Button variant="contained">order</Button>
        </div>
      </DialogContent>
    </Dialog>
  )

}

export default Cart;
