import { createContext, useState } from "react";

const ItemsContext = createContext({
  itemsData: [],
  switchPage: true,
  addNewItem: (item) => {},
  removeItem: (id) => {},
  updateItem: (id) => {},
  togglePage: () => {
    return true
  },
});

export default ItemsContext;
