  //     return res;
  //   }
  //   const meals = await getMeals(itemsData);
  //   dispatch({ type: "addNewItem", payload: meals });
  // }

  //get Meals Menu from Api
  const getItemsData = async () => {
    const res = await api.get("/meals");
    const data = res.data;
    setItemsData(data);
  }

  //Add new item to menu
  const addNewItem = async (itemData) => {
    const request = { id: Date.now(), ...itemData};
    const res = await api.post("/meals", request);
    setItemsData([...itemsData, res.data]);
  };

  const handleInput = (e) => {
    const { name, value } = e.target;
    setNewItem((previousItem) => ({
      ...previousItem, [name]: value
    })) 
  };

  const handleSubmitItem = (e) => {
    e.preventDefault();
    addNewItem(newItem);
    setNewItem({name:"", desc:"", img:"", price:""});
  }

  //Delete meal from menu
  const deleteItem = async (id) => {
    await api.delete(`/meals/${id}`);
    const updatedItemsList = itemsData.filter((itemData) => {
      return itemsData.id !== id;
    });
    setItemsData(updatedItemsList);
  }


  // const handleAddItem = async (item) => {
