import Cart from "../Components/Cart"
import CartContext from './CartContext'
import { useContext, useEffect, useState } from 'react';

export function useCartContext() {
  return useContext(CartContext);
}

const CartProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState([]);
  const [totalAmount, setTotalAmount] = useState(0);
  const [qty, setQty] = useState({});
  //Add item to cart
  const addItemToCart = (item) => {
    const updatedTotalAmount = totalAmount + item.price * qty;

    const inCartItemIndex = cartItems.findIndex((cartItem) => cartItem.id === item.id);
    const inCartItem = cartItems[inCartItemIndex];

    if (inCartItem) {
      setQty({
        ...qty,
        [item.name]: qty[item.name] + 1
      });

    } else {
      setQty({
        ...qty,
        [item.name]: 1
      });
      setCartItems([...cartItems, item]);
    }
    setTotalAmount(updatedTotalAmount);
  };



  useEffect(() => {
    const newTotalPrice = cartItems.reduce((item, currentValue) => {
      return (qty[item.name] * item.price) + currentValue;
    }, 0)
    setTotalAmount(newTotalPrice);

  }, [qty])



  const cartCtxValue = {
    cartItems,
    totalAmount,
    qty,
    addItem: addItemToCart,
  };

  return (
    <CartContext.Provider value={cartCtxValue}>
      { children }
    </CartContext.Provider>
  )
}
export default CartProvider;
