/* eslint-disable default-case */
import { useContext, useEffect, useReducer, useState } from 'react';
import ItemsContext from "./ItemsContext";
import api from "../Api/meals";

export function useItemContext() {
  return useContext(ItemsContext);
}

const initialState = {
  switchPage: true,
  itemsData: []
}

const reducer = (state, action) => {
  const { type, payload } = action;
  switch (type) {
    case "get": 
      return {...state, itemsData: payload};
    case "add":
      return {
        ...state,
        itemsData: [...state.itemsData, action.payload]
      }

    case "remove":
      const updatedItemsList = itemsData.filter((item) => {
        return item.id !== action.payload;
      })
      return {
        ...state, 
        itemsData: updatedItemsList
      }
  }
};

const ItemsProvider = ({ children }) => {
  const [state, dispatch] = useReducer(reducer, iniitialState);

  const getItemsData = async () => {
    const res = await api.get("/meals");
    const data = res.data;
    dispatch({
      type: "get",
      payload: {
        itemsData: data,
      },
    });
  };



  const [switchPage, setSwitchPage] = useState(true);
  const [itemsData, setItemsData] = useState([]);

  // const [state, dispatch] = useReducer(reducer, initialState);

    // {switchPage ? <Users /> : <Admin /> }

  //get itemsData from Api
  // const getItemsData = async () => {
  //   const res = await api.get("/meals");
  //   const data = res.data;
  //   setItemsData(data);
  // }

  useEffect(() => {
    getItemsData()
  }, [])


  //Add new item to menu
  const addItem = async (item) => {
    const request = { id: Date.now(), ...item};
    const res = await api.post("/meals", request);
    setItemsData([...itemsData, res.data]);
  };

  //Delete item from menu
  const deleteItem = async (id) => {
    await api.delete(`/meals/${id}`);
    const updatedItemsList = itemsData.filter((item) => {
      return item.id !== id;
    });
    setItemsData(updatedItemsList);
  }

  const itemCtxValue = {
    itemsData,
    switchPage,
    setSwitchPage,
    getItemsData,
    addItem,
    deleteItem
  }

  return (
    <ItemsContext.Provider value={itemCtxValue}>
      { children }
    </ItemsContext.Provider>
  );
}

export default ItemsProvider;
