import { Dialog, DialogContent, DialogTitle, List, ListItem } from "@mui/material";

const Cart = ({ isValid, hideModalHandler, cartItems }) => {


  return (
    <Dialog open={isValid} onClose={hideModalHandler}>
      <DialogContent>
        <DialogTitle>Your Cart</DialogTitle>
        <List>
          {cartItems.map((item) => {
            return (
              <ListItem key={item.id}>
                <div className="item-name">
                  <p>{item.name}</p>
                </div>
                <div className="item-price">
                  <p>{item.price} <span className="item-amount">{item.amount}</span></p>
                </div>




              </ListItem>
            )
          })}
        </List>
      </DialogContent>
    </Dialog>
  )

}

export default Cart;
