import { useState } from "react";
import { Button, Dialog, DialogContent, DialogTitle, Divider, List, ListItem } from "@mui/material";
import { useCartContext } from "../Store/CartProvider.js"

const Cart = ({ isValid, onClose }) => {
  const { cartItems, totalAmount, qty, setQty } = useCartContext();

  const handleAdd = () => {

  }

  const handleRemove = () => {

  }

  return (
    <Dialog className="cart-dialog" open={isValid} onClose={onClose}>
      <DialogContent>
        <DialogTitle>Your Cart</DialogTitle>
        <List className="cart-items">
          {cartItems.map((item) => {
            return (
              <ListItem className="cart-item" key={item.id}>
                <div className="item-details">
                  <div className="item-name">
                    <p>{item.name}</p>
                  </div>
                  <div className="item-price">
                    <p>${item.price} <span className="item-amount"> x {
                      qty[item.name]
                    }</span></p>
                  </div>
                </div>
                <div className="item-action">
                  <button onClick={handleAdd}>+</button>
                  <button onClick={handleRemove}>-</button>
                </div>
              </ListItem>
            )
          })}
        </List>
        <Divider sx={{ color: "red" }} />
        <div className="total-amount">
          <p>Total Amount:</p>
          <p>${totalAmount}</p>
        </div>
        <Button onClick={onClose}>Close</Button>
        <Button variant="contained">order</Button>
      </DialogContent>
    </Dialog>
  )

}

export default Cart;
