import { Box, Button, TextField } from "@mui/material";
import axios from "axios";
import CloudUploadIcon from '@mui/icons-material/CloudUpload';
import { useState } from "react";
import { useItemContext } from "../Store/ItemsProvider.js";

const apiKey = process.env.REACT_APP_API_KEY;

const AdminForm = ({ hideAddItemForm }) => {
  const [newItem, setNewItem] = useState({name:"", desc:"", price:"", file: ""});
  const { addItem } = useItemContext();

  const handleInput = (e) => {
    const { name, value } = e.target;
    setNewItem((previousItem) => ({
      ...previousItem, [name]: value
    })) 
  };

  // const toBase64 = file => new Promise((resolve, reject) => {
  //   const reader = new FileReader();
  //   reader.readAsDataURL(file);
  //   reader.onload = () => resolve(reader.result);
  //   reader.onerror = reject;
  // });

  const apiUrl = `https://api.imgbb.com/1/upload?&key=${apiKey}`;

  const uploadFile = () => {

    const file = document.getElementById('file-input');
    const formData = new FormData();
    formData.append('file', file);

    axios.post(apiUrl, formData)
    //   {
    //   headers: {
    //     // 'content-type': 'application/json',
    //     'Access-Control-Allow-Origin': '*',
    //   },
    // }
      .then((res) => {
      console.log(res);
      })
      .catch((error) => {
      console.log(error);
      });
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    // const fileInput = document.querySelector('#file-input') ;
    // const img = await toBase64(fileInput.files[0]);
    uploadFile()
    // addItem(newItem);
    // setNewItem({name:"", desc:"", price:"", file: ""});

  }

  return (
    <Box className="admin-form-container"> 
      <form onSubmit={handleSubmit}>
        <div className="admin-form-input">
          <TextField
            required
            fullWidth
            size="small"
            margin="dense"
            id="food-name"
            label="Name"
            variant="outlined"
            name="name"
            value={newItem.name}
            onChange={handleInput}
          /> 
          <TextField
            required
            fullWidth
            size="small"
            margin="dense"
            id="food-desc"
            label="Description"
            variant="outlined"
            name="desc"
            value={newItem.desc}
            onChange={handleInput}
          /> 
          <TextField
            required
            fullWidth
            size="small"
            margin="dense"
            id="food-price"
            label="Price"
            variant="outlined"
            name="price"
            value={newItem.price}
            onChange={handleInput}
          />
          {/* <TextField
            required
            fullWidth
            size="small"
            margin="dense"
            id="food-image"
            label="Image"
            variant="outlined"
            name="img"
            value={newItem.img}
            onChange={handleInput}
          /> */}
        </div>
        <div className="admin-form-upload">
            <label htmlFor="file-input"></label>
            <input required id="file-input" type="file" name="img"/>
        </div>
        <div className="admin-form-btn">
          <Button type="submit" size="small" variant="contained" color="success">
            Add
          </Button>
          <Button variant="contained" size="small" color="error" onClick={hideAddItemForm}>
            Cancel
          </Button>
        </div>
      </form>
    </Box>
  )
}

export default AdminForm;
