import { useEffect, useState } from 'react';
import { Grid, Typography } from "@mui/material";
import MealsItem from "./MealsItem";
import api from "../Api/meals";
const Meals = () => {
  const [meals, setMeals] = useState([]);
  const [newMeals, setNewMeals] = useState([]);
  const getMeals = async () => {
    const res = await api.get("/meals");
    const data = res.data;
    setMeals(data);
  }

  useEffect(() => {
    getMeals();
  }, [])

  const addMeals = async (meal) => {
    const newId = Math.max(...meals.map((m) => m.id), 0) + 1;
    const request = { id: newId, ...meal};
    const res = await api.post("/meals", request);
    setNewMeals([...meals, res.data]);
  };

  return (
    <div className="meals-container">
      <Typography variant="h4">Available Foods</Typography>
      <Grid className="meals-grid" container>
        {meals.map((meal, index) => (
          <Grid item xs={12} sm={6} md={4} lg={3}>
          <MealsItem 
            meal={meal}
          />
        </Grid>
        ))}

      </Grid>
    </div>
  )

}

export default Meals;
