import ItemsContext from "./ItemsContext";

const ItemsProvider = ({ children }) => {
  const [page, setPage] = useState(true)

  const togglePage = () => {
    setPage(!page);
  };

  return (
    <ItemsContext.Provider value={{ 
      page,
      togglePage
      // switchPage: page,
      // togglePage: () => {
      //   setPage(!page);
      // }
    }}>
      { children }
    </ItemsContext.Provider>
  );
}

export default ItemsProvider;
