import { useEffect, useState } from 'react';
import { Button, Box } from '@mui/material';
import AdminForm from "./AdminForm";
import Meals from "./Meals";
import api from "../Api/meals";

const Main = () => {
  const [isValid, setIsValid] = useState(false);

  const showAddItemForm = () => setIsValid(true);

  const hideAddItemForm = () => setIsValid(false);

  const [meals, setMeals] = useState([]);
  const getMeals = async () => {
    const res = await api.get("/meals");
    const data = res.data;
    setMeals(data);
  }

  useEffect(() => {
    getMeals();
  }, [])

  const addMeals = async (meal) => {
    const newId = Math.max(...meals.map((m) => m.id), 0) + 1;
    const request = { id: newId, ...meal};
    const res = await api.post("/meals", request);
    setMeals([...meals, res.data]);
  };

  return (
    <Box style={{ flex: 4 }}>
      {isValid ? (
        <AdminForm hideAddItemForm={hideAddItemForm} />
      ) : (
        <Button
          variant="contained"
          onClick={showAddItemForm}
          className="addItemsButton"
        >
          Add Food Item
        </Button>
      )}
      <Meals />
    </Box>
  )
}
export default Main;
