import { useEffect, useState } from 'react';
import { Grid, Typography } from "@mui/material";
import MealsItem from "./MealsItem";
import api from "../Api/meals";
const Meals = () => {
  const [meals, setMeals] = useState([]);

  const getMeals = async () => {
    const res = await api.get("meals");
    const data = res.data;
    setMeals(data);
  }

  useEffect(() => {
    getMeals();
  }, [])

  return (
    <div className="meals-container">
      <Typography variant="h4">Available Foods</Typography>
      <Grid className="meals-grid" container>
        {meals.map((meal, index) => (
          <Grid item xs={12} sm={6} md={4} lg={3}>
          <MealsItem name="Food 1" desc="Description 1" price="100" img="https://images.pexels.com/photos/2116475/pexels-photo-2116475.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"/>
        </Grid>
        ))}

      </Grid>
    </div>
  )

}

export default Meals;
