import { useContext, useEffect, useState, useReducer } from 'react';
import ItemsContext from "./ItemsContext";
import api from "../Api/meals";

export function useItemContext() {
  return useContext(ItemsContext);
}

function reducer(state, action)  {
  switch(action.type) {
    case "getItem": {
      const res = api.get("/meals");
      const data = res.data;
      return {
        ...state,
        itemsData: data
      };
    }
    case "addNewItem": {
      return {
        ...state,
        itemsData: [...state.itemsData, action.payload]
      };
    }
    case "removeItem": {
      const updatedItemData = state.itemsData.filter((item) => item.id !== action.payload);
      return {
        ...state,
        itemsData: updatedItemData
      }
    }
    case "updateItem": {
      return {
        ...state,
        itemsData: state.itemsData.map((item) => {
          if(item.id === action.payload.id) {
            return {
              ...item,
              ...action.payload
            }
          };
          return item;
        })
      }
    }
    default:
      return state;
  }
}

const ItemsProvider = ({ children }) => {
  const [meals, setMeals] = useState([]);

  //get Meals Menu from Api
  // const getMeals = async () => {
  //   const res = await api.get("/meals");
  //   const data = res.data;
  //   setMeals(data);
  // }

  // useEffect(() => {
  //   getMeals();
  // }, [])

  // const initialMenu = meals;

  const [state, dispatch] = useReducer(reducer, {
    itemsData: [],
    switchPage: false
  });


  const handleGetItem = async (data) => {
    await dispatch({ type: "getItem", payload: data });
  }

  useEffect(() => {
    handleGetItem();
  },[])

  const handleAddItem = async (item) => {
    await dispatch({ type: "addNewItem", payload: item });
  }

  // Delete meal from menu
  const handleDeleteItem = async (id) => {
    await api.delete(`/meals/${id}`);
    dispatch({ type: "removeItem", payload: id });
    // const updatedMealList = meals.filter((meal) => {
    //   return meal.id !== id;
    // });
    // setMeals(updatedMealList);
  }

  const itemCtxValue = {
    itemsData: state.itemsData,
    handleGetItem,
    handleAddItem,
    handleDeleteItem
  }



  return (
    <ItemsContext.Provider value={itemCtxValue}>
      { children }
    </ItemsContext.Provider>
  );
}

export default ItemsProvider;
