import { Box, Button, TextField } from "@mui/material";
import { useState } from "react";

const AdminForm = () => {
  const [newMeal, setNewMeal] = useState({name:"", desc:"", img:"", price:""});

  const handleSubmit = (e) => 
    e.preventDefault();
    if (newMeal.name === "" || newMeal.desc === "" || newMeal.img === "" || newMeal.price === "") {
      alert("All the fields are mandatory!");
      return;
    }

  const handleInput = () => {

  }

  return (
    <Box className="admin-form-container"> 
      <form onSubmit={handleSubmit}>
        <div className="admin-form-input">
          <TextField
            required
            id="food-name"
            label="Name"
            variant="outlined"
            value={newMeal.name}
            onChange={handleInput}
          /> 
          <TextField
            required
            id="food-desc"
            label="Description"
            variant="outlined"
            value={newMeal.desc}
            onChange={handleInput}
          /> 
          <TextField
            required
            id="food-price"
            label="Price"
            variant="outlined"
            value={newMeal.price}
            onChange={handleInput}
          />
        </div>
        <div className="admin-form-upload">
          <form>
            <label for="file"></label>
            <input type="file" id="file" name="filename"/>
          </form>
        </div>
        <div className="admin-form-btn">
          <Button type="submit" variant="contained" color="success">
            Add
          </Button>
          <Button variant="outlined" color="error">
            Cancel
          </Button>
        </div>
      </form>
    </Box>
  )
}

export default AdminForm;
